n, x = map(int, input().split())
res = 0

def amount_of_x(n, x):
    trans = 0
    while n >= 10:
        last_digit = n % 10
        if last_digit == x:
            trans += 1
        n //= 10

    if n == x:
        trans += 1

    return trans

for i in range(1, n+1):
    res += amount_of_x(i, x)

