def Merge(A, B):
    # åˆå¹¶ä¸¤ä¸ªåˆ—è¡¨
    res = []
    while len(A) != 0 and len(B) != 0:
        if A[0] <= B[0]:
            res.append(A.pop(0))
        else:
            res.append(B.pop(0))

    res.expend(A)
    res.expend(B)
    return res


def MergeSort(A):
    if len(A) < 2:
        return A

    mid = len(A) // 2
    left = MergeSort(A[:mid])
    right = MergeSort(A[mid:])

    return Merge(left, right)


n = int(input())
a = list(map(int, input().split()))

print(' '.join(map(str, MergeSort(a)))

