```python
def main():
    n = int(input())
    data = [int(x) for x in input().split()]
    dic = {}
    amount_1 = 0
    amount_2 = 0

    for i in data:
        dic[i] = dic.get(i, 0) + 1

    for i, j in dic.items():
        if j == 1:
            amount_1 += 1

        if j > 2:
            amount_2 += j - 2

    if amount_1 > amount_2:
        res = amount_2 + (amount_1 - amount_2) // 2
    else:
        res = amount_2

    print(res)


if __name__ == '__main__':
    main()
