def main():
    reminder = 1000000
    count = 0
    base = 7

    for exp in range(7, -1, -1):
        for j in range(5):
            if reminder - base**exp >= 0:
                reminder -= base**exp
                count += 1
            else:
                break

    print(count)  # 16

if __name__ == '__main__':
    main()
