```python
def solution2():
    t = int(input())
    
    # åˆå§‹åŒ–å­—å…¸æ¥ç®¡ç†æ‹›å‹Ÿäººæ•°å’Œæœ€å¤§å€¼
    counts = {i: 0 for i in range(1, 6)}
    max_counts = {i: 0 for i in range(1, 6)}

    for _ in range(t):
        number_list = list(map(int, input().split()))
        
        for i in range(1, 6):
            if i in number_list:
                counts[i] += 1  # å¦‚æœå½“å‰æ•°å­—åœ¨è¯¥è¡Œï¼Œè¿ç»­è®¡æ•°åŠ ä¸€
            else:
                # æ›´æ–°æœ€å¤§å€¼å¹¶é‡ç½®è¿ç»­è®¡æ•°
                max_counts[i] = max(max_counts[i], counts[i])
                counts[i] = 0

    # æ£€æŸ¥æœ€åä¸€æ¬¡è¿ç»­è®¡æ•°
    for i in range(1, 6):
        max_counts[i] = max(max_counts[i], counts[i])

    # æ‰“å°ç»“æœ
    print(' '.join(str(max_counts[i]) for i in range(1, 6)))


